        public static int Count(DataSet dataSet)
        {
            return dataSet != null && dataSet.Tables.Contains("Count")
                ? dataSet.Tables["Count"].AsEnumerable().Sum(dataRow => dataRow[0].ToInt())
                : 0;
        }

